INSERT INTO `wiki` (`status`,`cateid`,`src_type`,`src_url`,`title`,`content`,`version`,`ctime`,`ptime`,`utime`,`rel_news`,`rel_house`, `pinyin`, `firstletter`, `tags`) VALUES ('9','0','1','','房产类型','即房屋产权类型,目前市面上常见的房产类型分为商品房、保障房、自住型商品房(北京)、小产权房等。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanleixing','F','房屋产权 房产 商品房'), ('9','0','1','','房产税','房产税是以房屋为征税对象,按房屋的计税余值或租金收入为计税依据,向产权所有人征收的一种财产税。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanshui','F','房屋 房产税 收入'), ('9','0','1','','房产证 ','房产证（Premises Permit）购房者通过交易，取得房屋的合法所有权，可依法对所购房屋行使占有、使用、收益和处分的权利的证件。即《房屋所有权证》，是国家依法保护房屋所有权的合法凭证。房屋所有者凭证管理和使用自己的房屋。房产证包括房屋所有权证和房屋共有权证，在通常意义上，房产证是房屋所有权证的简称，是由不动产登记机关发放的证明房屋所有权归属的书面凭证。按照我国现行的房屋所有权登记制度，房屋登记是城镇房屋权利归属的法定公示方法。2011年8月下旬税务部门表示房产加名按房价一半征契税 。2013年4月，北京市住建委称，为防止虚假房屋买卖等行为，保障房屋交易安全，今后北京购房人拿到的房产证将可自愿并免费设立密码，房屋如需办理转让、变更、抵押等登记业务时，须提供密码才可办理。预计于2013年下半年实施。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanzheng ','F','房屋 房产证 证件'), ('9','0','1','','房产证抵押','双方凭借签订借款合同和担保抵押合同,然后到房管部门办理抵押登记。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanzhengdiya','F','合同'), ('9','0','1','','房产证面积计算','房产证注明的面积为房屋建筑面积,建筑面积应为套内面积和分摊的共有面积之和。其中,共有建筑面积的内容包括:电梯井、管道井、楼梯间、垃圾道等,独立使用的地下室和作为人防工程的地下室都不计入共有建筑面积。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanzhengmianjijisuan','F','面积 房产证 房屋'), ('9','0','1','','房产证期限','居住用地是70年,工业用地50年,商业用地40年,综合用地50年。《物权法》第一百四十九条规定:住宅建设用地使用权限期间届满的,自动续期。非住宅建设用地使用权期间届满后的续期,依照法律规定办理。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangchanzhengqixian','F','商业'), ('9','0','1','','房地产税','房地产税是一个综合性概念,即一切与房地产经济运动过程有直接关系的税都属于房地产税。在中国包括房地产业营业税、企业所得税、个人所得税、房产税、城镇土地使用税、城市房地产税、印花税、土地增值税、投资方向调节税、契税、耕地占用税等。','1479045248','1479045248','1479045248','1479045248','[]','[]','fangdichanshui','F','房地产税 房地产 经济'), ('9','0','1','','房改房','房改房,是指于1994年国务院发文实行的城镇住房制度改革的产物,是我国城镇住房由从前的单位分配转化为市场经济的一项过渡政策,现如今又可以叫做已购公有住房。已购公有住房,是指城镇职工根据国家和县级以上地方人民政府有关城镇住房制度改革政策规定,按照成本价或者标准价购买的已建公有住房。按照成本价购买的,房屋所有权归职工个人所有,按照标准价购买的,职工拥有部分房屋所有权,一般在5年后归职工个人所有。','1479045248','1479045248','1479045248','1479045248','[]','[]','fanggaifang','F','城镇 房屋 国务院'), ('9','0','1','','公积金','公积金,一般指公司公积金,是公司在资本之外所保留的资金金额。根据公积金提留是否为法律上的强制规定,可以将公积金分为法定公积金和任意公积金。此外住房公积金是指国家机关、国有企业、城镇集体企业、外商投资企业、城镇私营企业及其他城镇企业、事业单位及其在职职工缴存的长期住房储金。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijin','G','城镇 住房公积金'), ('9','0','1','','公积金贷款','公积金贷款是指缴存住房公积金的职工享受的贷款,国家规定,凡是缴存公积金的职工均可按公积金贷款的相关规定申请个人住房公积金贷款。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijindaikuan','G','公积金贷款 住房公积金 贷款'), ('9','0','1','','公积金提取','公积金提取是指缴存人按照公积金提取的要求,到公积金提取办理部门办理公积金提取手续,将公积金账户的金额转到个人账户上。公积金提取,公积金提取分为约定提取、部分提取和销户提取三种类型。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijintiqu','G',''), ('9','0','1','','公积金贷款买车','公积金能贷款买车吗 这个问题是值得探讨的,属于住房公积金的使用范围问题。因公积金贷款一直以其低首付低利率的特点吸引着众多的贷款人群,所以现在存在用公积金贷款买车的说法。今天小编就跟您一起来看一下住房公积金能贷款买车吗 究竟公积金买车会产生什么后果。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijindaikuanmaiche','G','贷款 住房公积金 公积金贷款'), ('9','0','1','','公积金还款','用公积金还款是一个不错的选择,由于银行住房贷款首付比例太高,要求太严,还得太多等等,让积蓄不够又有购房梦的朋友们对银行贷款望而却步,这时公积金还款就是能帮助你圆购房梦,下面介绍一些公积金还款方式。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijinhuankuan','G','还款 购房 银行'), ('9','0','1','','公积金利率','合理利用住房公积金可以帮助大家省去不少钱,尤其是用公积金来贷款买房,很多人会咨询有关2015年住房公积金利率的问题,这样能通过了解住房公积金利率,更好地知道公积金贷款额度等方面的信息,那么住房公积金利率是多少呢 ','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijinlilv','G','住房公积金 贷款买房'), ('9','0','1','','公积金买车','住房公积金顾名思义只是用来保障公民的住房问题,至于住房公积金能买车吗 这个答案肯定是不可以的,大家不要轻信那些说能住房公积金买车的小广告,谨防上当受骗。为了大家提高大家的警觉性,我来解释不能住房公积金买车的原因。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijinmaiche','G','住房公积金 广告'), ('9','0','1','','公积金贷款额度计算','针对公积金贷款额度怎么算,相信有部分朋友是感兴趣的,因为要用公积金贷款买房,那么公积金贷款额度上限是多少 到底在买房贷款的时候能够贷多少额度呢 这个公积金贷款额度怎么算,下面就为大家说明公积金贷款额度计算公式详解。','1479045248','1479045248','1479045248','1479045248','[]','[]','gongjijindaikuanedujisuan','G','贷款买房 买房 贷款'), ('